function clickHandle(e,t){let l,a,n;for(a=document.getElementsByClassName("content"),l=0;l<a.length;l++)a[l].style.display="none";for(n=document.getElementsByClassName("tab_btn"),l=0;l<n.length;l++)n[l].className=n[l].className.replace(" active","");document.getElementById(t).style.display="block",e.currentTarget.className+=" active"}function changeLanguage(e){let t=document.getElementsByClassName("lng");for(i=0;i<t.length;i++)t[i].className="lng";t[e].className+=" selected_lng"}