let isNavListVisible=!1;function clickHandle(e,t){let l,n,s;for(n=document.getElementsByClassName("content"),l=0;l<n.length;l++)n[l].style.display="none";for(s=document.getElementsByClassName("tab_btn"),l=0;l<s.length;l++)s[l].className=s[l].className.replace(" active","");document.getElementById(t).style.display="block",e.currentTarget.className+=" active"}function changeLanguage(e){let t=document.getElementsByClassName("lng");for(i=0;i<t.length;i++)t[i].className="lng";t[e].className+=" selected_lng"}function toggleList(){isNavListVisible=!isNavListVisible;let e=document.getElementById("navMenu");e.style.display=isNavListVisible?"flex":"none"}function hideList(){if(document.body.clientWidth<375){isNavListVisible=!1,document.getElementById("navMenu").style.display="none"}}